import React, { useState, useEffect } from "react";
//installed react-icons
import { FaBars } from "react-icons/fa";
import {
  Nav,
  NavBarContainer,
  NavLogo,
  MobileIcon,
  NavItem,
  NavMenu,
  NavLink,
} from "./NavBarElements";

function Navbar({ toggle }) {
  const [scrollNav, setScrollNav] = useState(false);

  const changeNav = () => {
    if (window.scrollY >= "8vh") {
      setScrollNav(true);
    } else {
      setScrollNav(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", changeNav);
  }, []);

  return (
    <>
      <Nav scrollNav={scrollNav}>
        <NavBarContainer>
          <NavLogo to="/">Josip Ku≈°tan</NavLogo>
          <MobileIcon onClick={toggle}>
            <FaBars />
          </MobileIcon>
          <NavMenu>
            <NavItem>
              <NavLink to="/work">Home</NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/work">My Work</NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/aboutMe">About Furno</NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/contactMe">Contact</NavLink>
            </NavItem>
          </NavMenu>
        </NavBarContainer>
      </Nav>
    </>
  );
}

export default Navbar;
