import styled from "styled-components";
import { MdKeyboardArrowRight, MdArrowForward } from "react-icons/md";

export default {
  colors: {
    black: "rgba(0,0,0,1)",
    green: "rgba(15,169,88,1)",
    transparentBlack: "rgba(0,0,0,0.25)",
    white: "rgba(255,255,255,1)",
  },
  fonts: {
    abel27Regular: {
      family: "Abel",
      size: "27px",
      weight: "400",
      lineHeight: "normal",
    },
    helveticaBold: {
      family: "Helvetica",
      size: "24px",
      weight: "700",
      lineHeight: "normal",
    },
    helveticaRegular: {
      family: "Helvetica",
      size: "16px",
      weight: "400",
      lineHeight: "30px",
    },
    kumarOne41Regular: {
      family: "Kumar One",
      size: "41px",
      weight: "400",
      lineHeight: "normal",
    },
    poppins21SemiBold: {
      family: "Poppins",
      size: "21px",
      weight: "600",
      lineHeight: "normal",
    },
    poppins25SemiBold: {
      family: "Poppins",
      size: "25px",
      weight: "600",
      lineHeight: "normal",
    },
    roboto24Bold: {
      family: "Roboto",
      size: "24px",
      weight: "700",
      lineHeight: "normal",
    },
  },
};

export const HeroContainer = styled.div`
  background: #0c0c0c;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  position: relative;
  z-index: 1;

  :before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: linear-gradient(
        180deg,
        rgba(0, 0, 0, 0.2) 0%,
        rgba(0, 0, 0, 0.5) 100%
      ),
      linear-gradient(180deg, rgba(0, 0, 0, 0.2) 0%, transparent);
    z-index: 2;
  }
`;

export const HeroBg = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
`;

export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const HeroContent = styled.div`
  z-index: 3;
  max-width: 70vw;
  position: absolute;
  padding: 8px 24px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const HeroH1 = styled.h1`
  color: #fff;
  font-size: 6vh;
  text-align: center;
  text-shadow: 2px 2px 2px #000;
`;

export const HeroP = styled.p`
  margin-top: 24px;
  color: #fff;
  font-size: 2vh;
  text-align: center;
  max-width: 600px;
`;

export const HeroBtnWrapper = styled.div`
  margin: 32px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const ArrowForward = styled(MdArrowForward)`
  margin-left: 8px;
  font-size: 20px;
`;

export const ArrowRight = styled(MdKeyboardArrowRight)`
  margin-left: 8px;
  font-size: 20px;
`;
