import React, { useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { Button } from "../ButtonElements";
import MyWorkArrowComponent from "../ArrowElement/index";
import {
  HeroContainer,
  HeroBg,
  VideoBg,
  HeroContent,
  HeroH1,
  HeroP,
  HeroBtnWrapper,
  ArrowForward,
  ArrowRight,
} from "./HeroSectionElements";
import Video from "../../videos/webreel_medium.mp4";
import { motion } from "framer-motion";

const HeroSection = () => {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };

  return (
    <HeroContainer id="home_page">
      <HeroBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </HeroBg>

      <HeroContent>
        <CSSTransition in={true} appear={true} timeout={600} classNames="fade">
          <HeroH1
            initial={{ scale: 0.9 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.3 }}
          >
            Make your imagination real!
          </HeroH1>
        </CSSTransition>
        <CSSTransition in={true} appear={true} timeout={600} classNames="fade">
          <HeroP
            initial={{ scale: 0.5, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: 0.3, duration: 0.3 }}
          >
            Contact me and let's make it real!
          </HeroP>
        </CSSTransition>
        <CSSTransition in={true} appear={true} timeout={1200} classNames="fade">
          <HeroBtnWrapper
            initial={{ scale: 0.5, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: 3, duration: 0.3 }}
          >
            <Button
              to="contactMe_page"
              smooth="true"
              duration={450}
              spy={true}
              exact="true"
              offset={-60}
              onMouseEnter={onHover}
              onMouseLeave={onHover}
            >
              Let's Talk! {hover ? <ArrowForward /> : <ArrowRight />}
            </Button>
          </HeroBtnWrapper>
        </CSSTransition>
      </HeroContent>
      <CSSTransition in={true} appear={true} timeout={2000} classNames="fade">
        <MyWorkArrowComponent />
      </CSSTransition>
    </HeroContainer>
  );
};

export default HeroSection;
