import React, { useState, useEffect, useRef } from "react";

import { useViewportScroll, motion, useTransform } from "framer-motion";

const { scrollY } = useViewportScroll();
const y1 = useTransform(scrollY, [0, 250], [-800, 0]);
const y2 = useTransform(scrollY, [70, 250], [-650, 0]);
const y3 = useTransform(scrollY, [30, 250], [-500, 0]);

const ParallaxBackground = () => {
  return (
    <>
      <div className="bodyDiv">
        <motion.div className="box" style={{ y: y1, x: -50 }} />
        <motion.div
          className="box"
          style={{ y: y2, x: 50, background: "salmon" }}
        />
        <motion.div
          className="box"
          style={{ y: y3, x: 0, background: "green" }}
        />
      </div>
    </>
  );
};

export default ParallaxBackground;
